import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ExpenseService } from '../../services/expense.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-expense-form',
  templateUrl: './expense-form.component.html',
  styleUrls: ['./expense-form.component.scss']
})
export class ExpenseFormComponent {
  expenseForm: FormGroup = new FormGroup({
    type: new FormControl('',[Validators.required, Validators.maxLength(100)]),
    amount: new FormControl('',[Validators.required]),
    description: new FormControl('',[Validators.required]),
    expense_date: new FormControl('',[Validators.required]),
  });

  constructor(
    private expenseService:ExpenseService,
    private router: Router
  ){}
  saveExpense(): void {
    this.expenseService.createExpense(this.expenseForm.value).subscribe( expense => {
      this.router.navigateByUrl('/gastos');
    })
  }
  hasError(field: string): boolean {
    const errorsObject = this.expenseForm.get(field)?.errors ?? {};
    const errors = Object.keys(errorsObject);
    if( errors.length && (this.expenseForm.get(field)?.touched || this.expenseForm.get(field)?.dirty) ){
      return true;
    }
    return false;
  }
  getCurrentError( field: string ):string {
    const errorsObject = this.expenseForm.get(field)?.errors ?? {};
    const errors = Object.keys(errorsObject);
    if(!errors) return '';
    return errors[0]
  }
}
<div class="d-flex align-items-center justify-content-between flex-wrap mt-5 mb-4 border-bottom">
  <h2 class="section-title mb-4">Nuevo gasto</h2>
  <div class="d-flex gap-2 mb-3">
    <a routerLink="/gasto" class="btn btn-outline-secondary">Cancelar</a>
    <button
      type="button"
      class="btn btn-primary"
      (click)="saveExpense()"
      [disabled]="expenseForm.invalid"
    >Guardar gasto</button>
  </div>
</div>
<form class="mb-5" [formGroup]="expenseForm" (ngSubmit)="saveExpense()">
  <div class="mb-3">
    <label for="typeInput" class="form-label">Tipo de gasto</label>
    <input
      type="text"
      formControlName="type"
      class="form-control {{ hasError('type') ? 'is-invalid' : '' }}"
      id="typeInput"
      placeholder="Transporte, Comida, Otros"
    >
    <div class="invalid-feedback" [ngSwitch]="getCurrentError('type')">
      <span *ngSwitchCase="'required'">El campo tipo es requerido</span>
      <span *ngSwitchCase="'maxlength'">El campo tipo debe contener 100 caracteres como máximo</span>
    </div>
  </div>
  <div class="mb-3">
    <label for="typeInput" class="form-label">Cantidad gastada</label>
    <div class="input-group has-validation">
      <span class="input-group-text">$</span>
      <input
        type="number"
        formControlName="amount"
        class="form-control {{ hasError('amount') ? 'is-invalid' : '' }}"
        id="amountInput"
        placeholder="0.00"
      >
      <div class="invalid-feedback" [ngSwitch]="getCurrentError('amount')">
        <span *ngSwitchCase="'required'">El campo cantidad es requerido</span>
      </div>
    </div>
  </div>
  <div class="mb-3">
    <label for="descriptionInput" class="form-label">Descripción</label>
    <textarea
      formControlName="description"
      class="form-control {{ hasError('description') ? 'is-invalid' : '' }}"
      id="descriptionInput"
      placeholder="Escribe una descripción del gasto realizado"
    ></textarea>
    <div class="invalid-feedback" [ngSwitch]="getCurrentError('description')">
      <span *ngSwitchCase="'required'">El campo cantidad es requerido</span>
    </div>
  </div>
  <div class="mb-3">
    <label for="expenseDateInput" class="form-label">Fecha de gasto</label>
    <input
        type="date"
        formControlName="expense_date"
        class="form-control {{ hasError('expense_date') ? 'is-invalid' : '' }}"
        id="expenseDateInput"
      >
      <div class="invalid-feedback" [ngSwitch]="getCurrentError('expense_date')">
        <span *ngSwitchCase="'required'">El campo fecha de gasto es requerido</span>
      </div>
  </div>
</form>
