import { Component, OnInit } from '@angular/core';
import { Expense } from 'src/app/models/expense';
import { ExpenseService } from 'src/app/services/expense.service';

@Component({
  selector: 'app-expense-list',
  templateUrl: './expense-list.component.html',
  styleUrls: ['./expense-list.component.scss']
})
export class ExpenseListComponent implements OnInit{
  expenses: Expense[] = [];
  total:number = 0;
  constructor(
    private expenseService: ExpenseService
  ){}
  ngOnInit(): void {
    this.loadDataIntoTable();
  }
  loadDataIntoTable():void {
    this.expenseService.getExpenses().subscribe( expenses => {
      this.expenses = expenses;
      this.calculateTotal();
    })
  }
  calculateTotal(): void {
    this.total = this.expenses.reduce((accumulated, currentValue) => {
      return accumulated + Number(currentValue.amount);
    }, 0)
  }
}
<div class="d-flex align-items-center justify-content-between gap-2 flex-wrap mt-5 mb-4">
    <h2>Gastos</h2>
    <div class="d-flex align-items-center flex-wrap gap-3 gap-sm-5">
        <p class="m-0">
            <span class=" fw-bold ms-1">
                <i class="ri-arrow-down-s-fill text-danger">
                    {{ total | currency }}
                </i>
            </span>
        </p>
        <a routerLink="/gastos/nuevo" class=" btn btn-primary">Agregar gasto</a>
    </div>
</div>
<div class="table-responsive mb-5">
    <table class="table">
        <thead>
            <tr>
                <th scope="col">#</th>
                <th scope="col">Tipo</th>
                <th scope="col">Cantidad</th>
                <th scope="col">Descripción</th>
                <th scope="col" width="180" >Fecha de gasto</th>
                <th scope="col"></th>
            </tr>
        </thead>
    </table>
</div>
